import React from 'react';
import '../styles/stepDot.css';

export default function StepDots({ steps = [], current = 0 }) {
  return (
    <div className="step-dots">
      {steps.map((s, i) => (
        <div key={s} className={`dot ${i === current ? 'active' : i < current ? 'done' : ''}`}>
          <span className="dot-index">{i + 1}</span>
          <span className="dot-label">{s}</span>
        </div>
      ))}
    </div>
  );
}
