import React, { useState, useEffect } from 'react';
import StepDots from '../components/StepDots';
import '../styles/registration.css';
import { get, set } from '../utils/storage';

const STEPS = [
  'Personal Information',
  'Address & Contact',
  'Emergency Contacts',
  'Password & Security',
  'Preferences',
  'Review & Confirm'
];

export default function Registration() {
  const [step, setStep] = useState(0);
  const [form, setFormState] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    emergency: '',
    password: ''
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    const saved = get('rah.registration', null);
    if (saved) setFormState(saved);
  }, []);

  useEffect(() => {
    set('rah.registration', form);
  }, [form]);

  function setForm(patch) {
    setFormState((s) => ({ ...s, ...patch }));
  }

  function validateStep(s) {
    const e = {};
    if (s === 0) {
      if (!form.name || form.name.length < 2) e.name = 'Enter your full name';
      if (!form.email || !/^[^@]+@[^@]+\.[^@]+$/.test(form.email)) e.email = 'Enter a valid email';
      if (!form.phone || form.phone.length < 6) e.phone = 'Enter a phone number';
    }
    if (s === 3) {
      if (!form.password || form.password.length < 6) e.password = 'Password must be 6+ chars';
    }
    setErrors(e);
    return Object.keys(e).length === 0;
  }

  function next() {
    if (!validateStep(step)) return;
    setStep((s) => Math.min(STEPS.length - 1, s + 1));
  }
  function back() {
    setStep((s) => Math.max(0, s - 1));
  }

  function submit() {
    if (!validateStep(step)) return;
    // For now, mark registration as complete and store profile in localStorage
    set('rah.profile', { ...form, createdAt: new Date().toISOString() });
    alert('Registration saved locally. You can view your Digital ID now.');
  }

  return (
    <section className="registration-page container">
      <h2 className="h2">Registration</h2>
      <StepDots steps={STEPS} current={step} />

      <div className="reg-card card">
        <h3>{STEPS[step]}</h3>
        <div className="reg-body">
          {step === 0 && (
            <div className="field-grid">
              <label>
                Name
                <input value={form.name} onChange={(e) => setForm({ name: e.target.value })} />
                {errors.name && <div className="muted" style={{color:'var(--color-danger)'}}>{errors.name}</div>}
              </label>
              <label>
                Email
                <input value={form.email} onChange={(e) => setForm({ email: e.target.value })} />
                {errors.email && <div className="muted" style={{color:'var(--color-danger)'}}>{errors.email}</div>}
              </label>
              <label>
                Phone
                <input value={form.phone} onChange={(e) => setForm({ phone: e.target.value })} />
                {errors.phone && <div className="muted" style={{color:'var(--color-danger)'}}>{errors.phone}</div>}
              </label>
            </div>
          )}

          {step === 1 && (
            <div className="field-grid">
              <label style={{gridColumn:'1 / -1'}}>
                Address
                <input value={form.address} onChange={(e) => setForm({ address: e.target.value })} />
              </label>
            </div>
          )}

          {step === 2 && (
            <div>
              <label>
                Emergency Contact
                <input value={form.emergency} onChange={(e) => setForm({ emergency: e.target.value })} />
              </label>
            </div>
          )}

          {step === 3 && (
            <div>
              <label>
                Password
                <input type="password" value={form.password} onChange={(e) => setForm({ password: e.target.value })} />
                {errors.password && <div className="muted" style={{color:'var(--color-danger)'}}>{errors.password}</div>}
              </label>
            </div>
          )}

          {step === 4 && (
            <div>
              <label>
                Notifications & Preferences
                <select value={form.pref || ''} onChange={(e) => setForm({ pref: e.target.value })}>
                  <option value="">Default</option>
                  <option value="sms">SMS</option>
                  <option value="email">Email</option>
                </select>
              </label>
            </div>
          )}

          {step === STEPS.length - 1 && (
            <div>
              <h4>Review</h4>
              <pre>{JSON.stringify(form, null, 2)}</pre>
            </div>
          )}
        </div>

        <div className="reg-actions">
          <button className="btn" onClick={back} disabled={step === 0}>
            Back
          </button>
          {step < STEPS.length - 1 ? (
            <button className="btn primary" onClick={next}>
              Next
            </button>
          ) : (
            <button
              className="btn primary"
              onClick={submit}
            >
              Submit
            </button>
          )}
        </div>
      </div>
    </section>
  );
}
