import React from 'react';
import { get } from '../utils/storage';

function qrUrl(data, size = 220) {
  const encoded = encodeURIComponent(JSON.stringify(data));
  return `https://chart.googleapis.com/chart?chs=${size}x${size}&cht=qr&chl=${encoded}`;
}

export default function DigitalID(){
  const profile = get('rah.profile', null);

  if (!profile) {
    return (
      <section className="container">
        <h2>Digital ID</h2>
        <p>No profile found. Please complete registration first.</p>
      </section>
    );
  }

  const idData = { name: profile.name, email: profile.email, phone: profile.phone };

  return (
    <section className="container">
      <h2>Digital ID</h2>
      <div style={{display:'flex',gap:20,alignItems:'center',marginTop:12}}>
        <div className="card">
          <h3 style={{marginBottom:6}}>{profile.name}</h3>
          <div className="muted">{profile.email}</div>
          <div className="muted">{profile.phone}</div>
          <div style={{marginTop:8,fontSize:13}}>Member since: {new Date(profile.createdAt).toLocaleDateString()}</div>
        </div>

        <div className="card" style={{textAlign:'center'}}>
          <img src={qrUrl(idData,300)} alt="Digital ID QR" style={{width:220,height:220}} />
          <div className="muted" style={{marginTop:10}}>Scan this QR to share your ID</div>
        </div>
      </div>
    </section>
  )
}
