import React, { useState, useEffect } from 'react';
import StepDots from '../components/StepDots';
import '../styles/registration.css';

const STEPS = [
  'Personal Information',
  'Address & Contact',
  'Emergency Contacts',
  'Password & Security',
  'Preferences',
  'Review & Confirm'
];

export default function Registration() {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({ name: '', email: '', phone: '' });

  useEffect(() => {
    const saved = localStorage.getItem('rah.registration');
    if (saved) setForm(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem('rah.registration', JSON.stringify(form));
  }, [form]);

  function next() {
    setStep((s) => Math.min(STEPS.length - 1, s + 1));
  }
  function back() {
    setStep((s) => Math.max(0, s - 1));
  }

  return (
    <section className="registration-page container">
      <h2>Registration</h2>
      <StepDots steps={STEPS} current={step} />

      <div className="reg-card">
        <h3>{STEPS[step]}</h3>
        <div className="reg-body">
          {step === 0 && (
            <div className="field-grid">
              <label>
                Name
                <input value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
              </label>
              <label>
                Email
                <input value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} />
              </label>
              <label>
                Phone
                <input value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })} />
              </label>
            </div>
          )}

          {step > 0 && step < STEPS.length - 1 && (
            <p>Form step placeholder for: {STEPS[step]}</p>
          )}

          {step === STEPS.length - 1 && (
            <div>
              <h4>Review</h4>
              <pre>{JSON.stringify(form, null, 2)}</pre>
            </div>
          )}
        </div>

        <div className="reg-actions">
          <button className="btn" onClick={back} disabled={step === 0}>
            Back
          </button>
          {step < STEPS.length - 1 ? (
            <button className="btn primary" onClick={next}>
              Next
            </button>
          ) : (
            <button
              className="btn primary"
              onClick={() => alert('Registration submitted (mock)')}
            >
              Submit
            </button>
          )}
        </div>
      </div>
    </section>
  );
}
