import React, { useState, useEffect } from 'react';
import { get, push } from '../utils/storage';

export default function CheckIn(){
  const [coords, setCoords] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    setHistory(get('rah.checkins', []));
  }, []);

  function doGeolocate(){
    if (!navigator.geolocation) return alert('Geolocation not supported');
    navigator.geolocation.getCurrentPosition((pos) => {
      const c = { lat: pos.coords.latitude, lng: pos.coords.longitude, at: new Date().toISOString() };
      setCoords(c);
    }, (err) => alert('Location error: ' + err.message));
  }

  function manualCheckIn(){
    const entry = { ...coords, at: new Date().toISOString(), note: 'Manual/Auto check-in' };
    push('rah.checkins', entry);
    setHistory(get('rah.checkins', []));
    alert('Checked in (saved locally)');
  }

  return (
    <section className="container">
      <h2>Check-In</h2>
      <p className="muted">Use your device to check in to a location. Stored locally for demo purposes.</p>

      <div style={{display:'flex',gap:12,marginTop:12}}>
        <button className="btn primary" onClick={doGeolocate}>Get Current Location</button>
        <button className="btn" onClick={manualCheckIn} disabled={!coords}>Save Check-In</button>
      </div>

      {coords && (
        <div className="card" style={{marginTop:12}}>
          <div>Latitude: {coords.lat}</div>
          <div>Longitude: {coords.lng}</div>
          <div className="muted">Captured: {new Date(coords.at || Date.now()).toLocaleString()}</div>
        </div>
      )}

      <div style={{marginTop:18}}>
        <h3>Check-in History</h3>
        {history.length === 0 && <div className="muted">No check-ins yet.</div>}
        <ul>
          {history.map((h, i) => (
            <li key={i} className="muted">{h.note || 'Check-in'} • {h.lat?.toFixed?.(5)} , {h.lng?.toFixed?.(5)} • {new Date(h.at).toLocaleString()}</li>
          ))}
        </ul>
      </div>
    </section>
  )
}
